import React from 'react'
import { Card, CardBody, ButtonGroup, Button } from 'reactstrap';
import styles from './Products.module.css'
import { useHistory, useParams } from 'react-router-dom';
import CartSvg from '../../assets/icons/cart.svg'
import { addItemToCart } from '../../redux/actions';
import { useState } from 'react';

function ListItem({ item }) {
    const params = useParams();
    const [data, setData] = useState(null);
    const history = useHistory()
    const handleCartClick = (e)=>{
        e.stopPropagation()
    }
    return (
        <Card onClick={() => history.replace("/products/" + item.slug)}>
            <CardBody>
                <div className={styles.imageContainer}>
                    <img className={styles.image} alt={item.title} src={item.image} />
                </div>
                <h5>{item.title}</h5>
                <p>{item.description}</p>
                {data ? (
                <ButtonGroup className='w-100'>
                    <Button color='primary'>{item.price} $</Button> 
                    {isInCart(data, props.cartItems) ? (
                        <Button 
                        color="warning"
                        onClick={()=>props.removeItemFromCart(data.slug)}
                        >
                            Remove from cart
                        </Button>
                    ) : (
                            <Button 
                                onClick={()=>addItemToCart(data)}
                                color='success'>
                                <img
                                    className="ml-1"
                                    width="20"
                                    height="20"
                                    alt="cart"
                                    src={CartSvg} />
                            </Button>
                    )}
                </ButtonGroup>

                ) : (
                    <h4>
                        Error 404: Product {params.slug} is not defined
                    </h4>
                )}
            </CardBody>
        </Card>
    )
}

export default ListItem
