import React, { useEffect } from 'react'
import { Card, CardBody, ButtonGroup, Button } from 'reactstrap';
import styles from './Products.module.css'
import { useHistory, useParams } from 'react-router-dom';
import CartSvg from '../../assets/icons/cart.svg'
import { addItemToCart } from '../../redux/actions';
import { useState } from 'react';
import Axios from 'axios';

function ListItem(props) {
    const params = useParams();
    const [data, setData] = useState(null);
    const history = useHistory()

    const isInCart = (item, cartItems) => {
        return !!cartItems.find((a) => a.slug === item.slug);
    }
    useEffect(() => {
        // setLoading(true)
        Axios.get(process.env.REACT_APP_API_URL + "/api/products/" + params.slug)
            .then(({ data }) => {

                setData(data);
            })
            .finally(() => {
                // setLoading(false)
            })
    }, [params.slug])

    const handleCartClick = (e)=>{
        e.stopPropagation()
    }
    return (
        <div>
            {data ? (
            <Card onClick={() => history.replace("/products/" + props.slug)}>
                <CardBody>
                    <div className={styles.imageContainer}>
                        <img className={styles.image} alt={item.title} src={item.image} />
                    </div>
                    <h5>{item.title}</h5>
                    <p>{item.description}</p>
                    <ButtonGroup className='w-100'>
                        <Button color='primary'>{item.price} $</Button> 
                        {isInCart(data, props.cartItems) ? (
                            <Button 
                            color="warning"
                            onClick={()=>props.removeItemFromCart(data.slug)}
                            >
                                Remove from cart
                            </Button>
                        ) : (
                                <Button 
                                    onClick={()=>props.addItemToCart(data)}
                                    color='success'>
                                    <img
                                        className="ml-1"
                                        width="20"
                                        height="20"
                                        alt="cart"
                                        src={CartSvg} />
                                </Button>
                        )}
                    </ButtonGroup>
                </CardBody>
            </Card>
            ) : (
                <h4>
                    Error 404: Product {params.slug} is not defined
                </h4>
            )}
        </div>
        )
    }

export default ListItem
